library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity autoencoder_tb is
end autoencoder_tb;

architecture Behavioral of autoencoder_tb is

component autoencoder
    Generic(
--        RAMaddressbit : integer := 13;
--        RAMaddressnum : integer := 8192;
        axi_bit : integer := 32;--PC通信時の入出力データのbit数。32bit固定。
        data_dim : integer := 1024 ; --入力・出力層の大きさ(32×32=1024)
        hide_dim : integer := 32 ; --隠れ層の大きさ
        data_bit : integer := 8 ; --入出力データのbit数
        weight_bit : integer := 16 ; --重みデータのbit数
        bias_bit : integer := 16 ;  --バイアスデータのbit数
        hide_bit : integer := 24 --隠れ層データのbit数
    );
    Port(
        CLK : in std_logic;
        X : in std_logic_vector(data_dim * data_bit -1 downto 0);
        ENCODE_START : in std_logic;
        Y : out std_logic_vector(data_dim * data_bit -1 downto 0);
        Y_ENABLE : out std_logic
    );
end component;

signal CLK_tb : std_LOGIC := '0';
signal ENCODE_START_tb : std_LOGIC := '0';
signal X_tb : std_logic_vector (8191 downto 0) := (others => '0');
signal Y_tb : std_logic_vector (8191 downto 0) := (others => '0');
signal Y_ENABLE_tb : std_LOGIC := '0';

begin
simulation_autoencoder : autoencoder port map(
    CLK => CLK_tb,
    ENCODE_START => ENCODE_START_tb,
    X  => X_tb,
    Y  => Y_tb,
    Y_ENABLE => Y_ENABLE_tb
);

process
begin
CLK_tb <= '0';
wait for 1 ns;
CLK_tb <= '1';
wait for 1 ns;
end process;

process
begin
ENCODE_START_tb <= '0';
wait for 3 ns;
wait for 100 ns;
ENCODE_START_tb <= '1';
wait for 2 ns;
ENCODE_START_tb <= '0';
wait for 40000 ns;
ENCODE_START_tb <= '1';
wait for 2 ns;
ENCODE_START_tb <= '0';
wait for 100000 ns;
end process;


process
begin
X_tb <=
--""
""
;
wait for 5 ns;
end process;




end Behavioral;
